PreferredSizeWidget _buildAppBar(BuildContext context) { return CustomAppBar(height: 70.v, leadingWidth: 35.h, leading: AppbarLeadingImage(imagePath: ImageConstant.imgArrowLeft2, margin: EdgeInsets.only(left: 21.h, top: 40.v, bottom: 6.v), onTap: () {onTapArrowLeftTwo(context);}), title: AppbarTitle(text: "Notification", margin: EdgeInsets.only(left: 10.h, top: 32.v, bottom: 1.v)), styleType: Style.bgShadow_1); } 
